create table internship (id int8 generated by default as identity, date_begin date, date_end date, date_end_recording date, description varchar(255), name varchar(255), state varchar(255), primary key (id));
create table lessons (id int8 generated by default as identity, name varchar(255), publication_date_time timestamp, status varchar(255), internship_id int8 not null, primary key (id));
create table participants (id int8 generated by default as identity, about_me varchar(255), city varchar(255), course int4 not null, date_of_birth date, education_status varchar(255), email varchar(255), faculty varchar(255), full_name varchar(255), mobile_number varchar(255), specialty varchar(255), telegram_id varchar(255), university varchar(255), username varchar(255), primary key (id));
create table performance (id int8 generated by default as identity, grade varchar(255), participant_id int8 not null, task_id int8 not null, primary key (id));
create table tasks (id int8 generated by default as identity, admin_comment varchar(255), git_lab_repository_id varchar(255), grade varchar(255), name varchar(255), status varchar(255), lesson_id int8 not null, primary key (id));
alter table if exists lessons add constraint FKfl9grtmnbg8ljpneb3bonh862 foreign key (internship_id) references internship;
alter table if exists performance add constraint FK9bd0hwg03p2l0cbrhweh7fwum foreign key (participant_id) references participants;
alter table if exists performance add constraint FKk4ic5290gc7a4jhnxalt2i2xd foreign key (task_id) references tasks;
alter table if exists tasks add constraint FKij2nuclq3hx0l5tanobfknbpw foreign key (lesson_id) references lessons;